DROP TABLE IF EXISTS `hr_staff_info`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `hr_staff_info` (
  `STAFF_ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增字段',
  `CREATE_USER_ID` varchar(254) NOT NULL COMMENT '创建人',
  `CREATE_DEPT_ID` int(11) NOT NULL COMMENT '创建者部门编号',
  `USER_ID` varchar(254) NOT NULL COMMENT '用户名',
  `DEPT_ID` int(11) NOT NULL DEFAULT '0' COMMENT '部门',
  `STAFF_NO` varchar(254) NOT NULL COMMENT '员工编号',
  `WORK_NO` varchar(254) NOT NULL COMMENT '员工工号',
  `PHOTO_NAME` varchar(254) NOT NULL COMMENT '照片文件名',
  `WORK_TYPE` varchar(254) NOT NULL COMMENT '工种',
  `STAFF_NAME` varchar(254) NOT NULL COMMENT '员工姓名',
  `STAFF_E_NAME` varchar(254) NOT NULL COMMENT '英文名',
  `STAFF_CARD_NO` varchar(254) NOT NULL COMMENT '身份证号码',
  `STAFF_SEX` varchar(4) NOT NULL COMMENT '性别(0-男,1-女)',
  `STAFF_BIRTH` date NOT NULL DEFAULT '0000-00-00' COMMENT '出生日期',
  `IS_LUNAR` char(1) NOT NULL DEFAULT '0' COMMENT '出生日期是否是农历(0-否,1-是)',
  `STAFF_AGE` varchar(8) NOT NULL COMMENT '年龄',
  `STAFF_NATIVE_PLACE` varchar(254) NOT NULL COMMENT '籍贯',
  `STAFF_DOMICILE_PLACE` varchar(254) NOT NULL COMMENT '户口所在地',
  `YES_OTHER_P` char(1) NOT NULL COMMENT '是否异地户口',
  `STAFF_NATIONALITY` varchar(16) NOT NULL COMMENT '民族',
  `STAFF_MARITAL_STATUS` varchar(16) NOT NULL COMMENT '婚姻状况',
  `STAFF_POLITICAL_STATUS` varchar(16) CHARACTER SET gb2312 NOT NULL COMMENT '政治面貌',
  `JOIN_PARTY_TIME` date NOT NULL DEFAULT '0000-00-00' COMMENT '入党团时间',
  `STAFF_PHONE` varchar(254) NOT NULL COMMENT '联系电话',
  `STAFF_MOBILE` varchar(254) NOT NULL COMMENT '手机号码',
  `STAFF_LITTLE_SMART` varchar(254) NOT NULL COMMENT '小灵通号码',
  `STAFF_EMAIL` varchar(254) NOT NULL COMMENT 'E_MAIL',
  `STAFF_MSN` varchar(254) NOT NULL COMMENT 'MSN',
  `STAFF_QQ` varchar(254) NOT NULL COMMENT 'QQ',
  `HOME_ADDRESS` varchar(254) NOT NULL COMMENT '家庭地址',
  `OTHER_CONTACT` varchar(254) NOT NULL COMMENT '其它联系方式',
  `JOB_BEGINNING` date NOT NULL DEFAULT '0000-00-00' COMMENT '参加工作时间',
  `WORK_AGE` varchar(16) NOT NULL COMMENT '总工龄',
  `STAFF_HEALTH` varchar(16) NOT NULL COMMENT '健康状况',
  `STAFF_HIGHEST_SCHOOL` varchar(32) NOT NULL COMMENT '最高学历',
  `STAFF_HIGHEST_DEGREE` varchar(32) NOT NULL COMMENT '最高学位',
  `GRADUATION_DATE` date NOT NULL DEFAULT '0000-00-00' COMMENT '毕业时间',
  `GRADUATION_SCHOOL` varchar(254) NOT NULL COMMENT '毕业学校',
  `STAFF_MAJOR` varchar(32) NOT NULL COMMENT '专业',
  `COMPUTER_LEVEL` varchar(32) NOT NULL COMMENT '计算机水平',
  `FOREIGN_LANGUAGE1` varchar(32) NOT NULL COMMENT '外语语种1',
  `FOREIGN_LEVEL1` varchar(32) NOT NULL COMMENT '外语水平1',
  `FOREIGN_LANGUAGE2` varchar(32) NOT NULL COMMENT '外语语种2',
  `FOREIGN_LEVEL2` varchar(32) NOT NULL COMMENT '外语水平2',
  `FOREIGN_LANGUAGE3` varchar(32) NOT NULL COMMENT '外语语种3',
  `FOREIGN_LEVEL3` varchar(32) NOT NULL COMMENT '外语水平3',
  `STAFF_SKILLS` varchar(254) NOT NULL COMMENT '特长',
  `STAFF_OCCUPATION` varchar(32) NOT NULL COMMENT '员工类型',
  `ADMINISTRATION_LEVEL` varchar(32) NOT NULL COMMENT '行政等级',
  `JOB_POSITION` varchar(32) NOT NULL COMMENT '职务',
  `PRESENT_POSITION` varchar(32) NOT NULL COMMENT '职称',
  `DATES_EMPLOYED` date NOT NULL DEFAULT '0000-00-00' COMMENT '入职时间',
  `JOB_AGE` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '本单位工龄',
  `BEGIN_SALSRY_TIME` date NOT NULL DEFAULT '0000-00-00' COMMENT '起薪时间',
  `WORK_STATUS` varchar(32) NOT NULL COMMENT '在职状态',
  `STAFF_CS` date NOT NULL DEFAULT '0000-00-00' COMMENT '合同签订时间',
  `STAFF_CTR` date NOT NULL DEFAULT '0000-00-00' COMMENT '合同到期时间',
  `REMARK` text NOT NULL COMMENT '备注',
  `STAFF_COMPANY` varchar(254) NOT NULL COMMENT '所在单位',
  `RESUME` text NOT NULL COMMENT '简历',
  `ATTACHMENT_ID` text NOT NULL COMMENT '附件编号',
  `ATTACHMENT_NAME` text NOT NULL COMMENT '附件名称',
  `ADD_TIME` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '建档时间',
  `LAST_UPDATE_TIME` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '最后修改时间',
  `LEAVE_TYPE` decimal(4,1) NOT NULL DEFAULT '0.0' COMMENT '年休假',
  `STAFF_TYPE` varchar(254) NOT NULL COMMENT '户口类别',
  `YES_OR_NOT` varchar(8) NOT NULL COMMENT 'OA登录权限(1-允许登录,""-不允许登录)',
  `CERTIFICATE` text NOT NULL COMMENT '职务情况',
  `SURETY` text NOT NULL COMMENT '担保记录',
  `BODY_EXAMIM` text NOT NULL COMMENT '体检记录',
  `INSURE` text NOT NULL COMMENT '社保缴纳情况',
  `USERDEF1` text NOT NULL COMMENT '自定义字段1',
  `USERDEF2` text NOT NULL COMMENT '自定义字段2',
  `USERDEF3` text NOT NULL COMMENT '自定义字段3',
  `USERDEF4` text NOT NULL COMMENT '自定义字段4',
  `USERDEF5` text NOT NULL COMMENT '自定义字段5',
  `RECORD_DATE` date NOT NULL COMMENT '记录日期',
  `WORK_LEVEL` varchar(200) NOT NULL COMMENT '职称级别',
  `STAFF_NATIVE_PLACE2` varchar(254) NOT NULL COMMENT '籍贯',
  `WORK_JOB` varchar(200) NOT NULL COMMENT '岗位',
  `BEFORE_NAME` varchar(200) NOT NULL COMMENT '曾用名',
  `BIRTH_REMIND_DATE` varchar(10) NOT NULL COMMENT '生日提醒日期',
  `BANK1` varchar(254) NOT NULL COMMENT '开户行名1',
  `BANK_ACCOUNT1` varchar(254) NOT NULL COMMENT '开户行的账号1',
  `BANK2` varchar(254) NOT NULL COMMENT '开户行名2',
  `BANK_ACCOUNT2` varchar(254) NOT NULL COMMENT '开户行的账号2',
  `BLOOD_TYPE` varchar(5) NOT NULL COMMENT '血型(A-A型血,B-B型血,O-O型血,AB-AB型血)',
  `IS_EXPERTS` char(2) NOT NULL COMMENT '是否为专家',
  `EXPERTS_INFO` text COMMENT '专家特长信息',
  `DIRECTLY_UNDER` varchar(200) NOT NULL COMMENT '直属下级',
  `DIRECTLY_SUPERIOR` varchar(200) NOT NULL COMMENT '直属上级',
  `PART_TIME` varchar(200) NOT NULL COMMENT '兼职',
  `RESEARCH_RESULTS` text NOT NULL COMMENT '研究成果',
  `OFF_DURATION` varchar(7) NOT NULL COMMENT '倒休时长(天、小时)',
  PRIMARY KEY (`STAFF_ID`),
  UNIQUE KEY `USER_ID_2` (`USER_ID`),
  KEY `USER_ID` (`USER_ID`),
  KEY `STAFF_NO` (`STAFF_NO`)
) ENGINE=InnoDB AUTO_INCREMENT=25 DEFAULT CHARSET=gbk COMMENT='人事档案的信息';
