  `rss_link` varchar(255) NOT NULL COMMENT 'RSS订阅',
  `custom_json` varchar(255) NOT NULL COMMENT '自定义参数',
  `state` tinyint(1) unsigned NOT NULL COMMENT '启用状态(0:未启用1:启用)',
  `corp_id` varchar(38) NOT NULL COMMENT '企业ID',
  PRIMARY KEY (`id`),
  KEY `mid` (`mid`),
  KEY `type` (`type`),
  KEY `corp_id` (`corp_id`),
  KEY `comtype` (`comtype`)
) ENGINE=InnoDB AUTO_INCREMENT=267 DEFAULT CHARSET=gbk COMMENT='组件';
