CREATE TABLE `portal_contents` (
  `content_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '内容ID（自增唯一）',
  `portal_id` smallint(5) unsigned NOT NULL COMMENT '内容所属门户ID',
  `column_id` smallint(5) unsigned NOT NULL COMMENT '内容所属栏目ID',
  `content_type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '内容的类型(0-文本型,1-链接地址,2-文件型)',
  `subject` varchar(100) NOT NULL DEFAULT '' COMMENT '内容标题',
  `content_link` varchar(100) NOT NULL DEFAULT '' COMMENT '内容链接地址',
  `link_target` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '内容链接的打开方式(0-页面默认,1-新窗口,2-当前窗口,3-父窗口,4-顶层窗口)',
  `link_img_id` varchar(30) NOT NULL DEFAULT '' COMMENT '链接显示图片附件id',
  `link_img_name` varchar(300) NOT NULL DEFAULT '' COMMENT '链接显示图片附件名称',
  `publish_time` int(10) unsigned NOT NULL COMMENT '内容发布时间',
  `component_id` varchar(100) NOT NULL COMMENT '内容所属组件ID',
  `corp_id` varchar(38) NOT NULL COMMENT '企业ID',
  `create_uid` varchar(40) NOT NULL COMMENT '创建者UID',
  `keyword` varchar(100) NOT NULL COMMENT '内容关键词',
  `home_page_push` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否首页推送',
  `link_column_id` int(10) NOT NULL COMMENT '链接至栏目id',
  `from_module` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '从哪个模块同步过来的内容（1-公告，2-新闻）',
  `res_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '对应模块数据id',
  `category` varchar(100) NOT NULL COMMENT '类别',
  `summary` mediumtext NOT NULL COMMENT '摘要',
  `source` varchar(100) NOT NULL COMMENT '来源',
  `modify_uid` varchar(40) NOT NULL COMMENT '修改人uid',
  PRIMARY KEY (`content_id`),
  KEY `portal_id` (`portal_id`),
  KEY `column_id` (`column_id`)
) ENGINE=InnoDB AUTO_INCREMENT=483 DEFAULT CHARSET=gbk COMMENT='门户内容主表';
