CREATE TABLE `user` (
  `UID` int(11) NOT NULL AUTO_INCREMENT COMMENT '唯一自增ID',
  `USER_ID` varchar(40) NOT NULL COMMENT '用户名',
  `USER_NAME` varchar(60) NOT NULL DEFAULT '' COMMENT '用户真实姓名',
  `USER_NAME_INDEX` varchar(60) NOT NULL DEFAULT '' COMMENT '用户姓名索引',
  `BYNAME` varchar(20) NOT NULL DEFAULT '' COMMENT '登录名',
  `USEING_KEY` char(1) NOT NULL DEFAULT '0' COMMENT '使用USB KEY登录',
  `USING_FINGER` char(1) NOT NULL DEFAULT '0' COMMENT '使用指纹验证',
  `PASSWORD` varchar(50) NOT NULL DEFAULT '' COMMENT '用户密码',
  `KEY_SN` varchar(20) NOT NULL DEFAULT '' COMMENT '绑定USB Key序列号',
  `SECURE_KEY_SN` varchar(20) NOT NULL COMMENT '动态密码卡号',
  `USER_PRIV` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '角色编号',
  `USER_PRIV_NO` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '角色排序号',
  `USER_PRIV_NAME` varchar(40) NOT NULL DEFAULT '' COMMENT '角色名称',
  `POST_PRIV` char(1) NOT NULL DEFAULT '0' COMMENT '管理范围',
  `POST_DEPT` varchar(1000) NOT NULL DEFAULT '' COMMENT '管理范围指定部门',
  `DEPT_ID` int(11) NOT NULL DEFAULT '0' COMMENT '部门ID',
  `DEPT_ID_OTHER` varchar(100) NOT NULL DEFAULT '' COMMENT '辅助部门',
  `VIEW_DEPT_ID` varchar(100) NOT NULL DEFAULT '' COMMENT '授权查看部门id串',
  `VIEW_UID` varchar(100) NOT NULL DEFAULT '' COMMENT '授权查看人员uid串',
  `VIEW_PRIV_ID` varchar(100) NOT NULL DEFAULT '' COMMENT '授权查看角色id串',
  `LEAVE_DEPT` int(11) NOT NULL COMMENT '离职部门ID',
  `SEX` char(1) NOT NULL DEFAULT '' COMMENT '性别',
  `BIRTHDAY` date NOT NULL DEFAULT '0000-00-00' COMMENT '生日',
  `IS_LUNAR` char(1) NOT NULL DEFAULT '0' COMMENT '是否农历(1-是,0-否)',
  `TEL_NO_DEPT` varchar(50) NOT NULL DEFAULT '' COMMENT '工作电话',
  `FAX_NO_DEPT` varchar(50) NOT NULL DEFAULT '' COMMENT '工作传真',
  `ADD_HOME` varchar(200) NOT NULL DEFAULT '' COMMENT '家庭住址',
  `POST_NO_HOME` varchar(50) NOT NULL DEFAULT '' COMMENT '家庭邮编',
  `TEL_NO_HOME` varchar(50) NOT NULL DEFAULT '' COMMENT '家庭电话',
  `MOBIL_NO` varchar(50) NOT NULL DEFAULT '' COMMENT '手机号',
  `BP_NO` varchar(50) NOT NULL DEFAULT '' COMMENT 'BP机号',
  `EMAIL` varchar(50) NOT NULL DEFAULT '' COMMENT '电子邮件地址',
  `OICQ_NO` varchar(50) NOT NULL DEFAULT '' COMMENT 'QQ号码',
  `ICQ_NO` varchar(50) NOT NULL DEFAULT '' COMMENT 'ICQ号码',
  `MSN` varchar(200) NOT NULL DEFAULT '' COMMENT 'MSN号码',
