  `CALL_SOUND` char(1) NOT NULL DEFAULT '1' COMMENT '短信提示音',
  `LAST_VISIT_TIME` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '上次访问系统的时间',
  `SMS_ON` char(1) NOT NULL DEFAULT '' COMMENT '短信提醒窗口弹出方式(1-自动,0-手动)',
  `MENU_TYPE` char(1) NOT NULL DEFAULT '1' COMMENT '登录模式(1-在本窗口打开OA,2-在新窗口打开OA显示工具栏,3-在新窗口打开)',
  `LAST_PASS_TIME` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '上次修改密码的时间',
  `THEME` tinyint(3) unsigned NOT NULL DEFAULT '10' COMMENT '界面主题',
  `SHORTCUT` text NOT NULL COMMENT '快捷菜单ID串',
  `PORTAL` varchar(20) NOT NULL DEFAULT '' COMMENT '门户ID串',
  `PANEL` char(1) NOT NULL DEFAULT '1' COMMENT '登录后显示的左侧面板(1-导航,2-组织,3-短信,4-搜索)',
  `ONLINE` int(11) NOT NULL DEFAULT '0' COMMENT '在线时长',
  `ON_STATUS` char(1) NOT NULL DEFAULT '1' COMMENT '记录在线状态(1-联机,2-忙碌,3-离开)',
  `ATTEND_STATUS` char(1) NOT NULL DEFAULT '0' COMMENT '岗位状态(1-出差,2-请假,3-外出)',
  `MOBIL_NO_HIDDEN` char(1) NOT NULL DEFAULT '0' COMMENT '手机号码是否公开(0-公开,1-不公开)',
