  `MYTABLE_RIGHT` varchar(200) NOT NULL DEFAULT 'ALL' COMMENT '右侧的桌面模块',
  `USER_PRIV_OTHER` varchar(100) NOT NULL DEFAULT '' COMMENT '辅助角色编码串',
  `USER_NO` smallint(5) unsigned NOT NULL DEFAULT '10' COMMENT '用户排序号',
  `NOT_LOGIN` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '禁止登录OA系统(1-禁止,0-不禁止)',
  `NOT_VIEW_USER` char(1) NOT NULL DEFAULT '0' COMMENT '禁止查看用户列表(1-禁止,0-不禁止)',
  `NOT_VIEW_TABLE` char(1) NOT NULL DEFAULT '0' COMMENT '禁止显示桌面(1-禁止,0-不禁止)',
  `NOT_SEARCH` char(1) NOT NULL DEFAULT '0' COMMENT '禁止OA搜索(1-禁止,0-不禁止)',
  `BKGROUND` varchar(200) NOT NULL DEFAULT '' COMMENT '桌面背景图片',
  `BIND_IP` varchar(200) NOT NULL DEFAULT '' COMMENT '绑定IP地址',
  `LAST_VISIT_IP` varchar(20) NOT NULL DEFAULT '' COMMENT '上次访问系统的IP',
  `MENU_IMAGE` varchar(10) NOT NULL DEFAULT '0' COMMENT '菜单图标(0-每个菜单使用不同图标,1-不显示菜单图标)',
  `WEATHER_CITY` varchar(50) DEFAULT NULL,
  `SHOW_RSS` char(1) NOT NULL DEFAULT '1' COMMENT '是否显示今日资讯(1-显示,0-不显示)',
  `MY_RSS` text NOT NULL COMMENT '今日资讯中的网址设置',
  `REMARK` text NOT NULL COMMENT '备注',
  `MENU_EXPAND` char(4) DEFAULT NULL COMMENT '默认展开菜单',
  `MY_STATUS` varchar(200) NOT NULL DEFAULT '' COMMENT '个性签名',
  `LIMIT_LOGIN` char(1) NOT NULL DEFAULT '0' COMMENT '登录次数限制',
  `PHOTO` varchar(20) NOT NULL COMMENT '用户名片图片',
  `IM_RANGE` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '即时通讯使用权限(1-允许使用,2-禁止使用)',
  `LEAVE_TIME` datetime NOT NULL COMMENT '早退时间',
  `SECRET_LEVEL` int(2) DEFAULT '0' COMMENT '邮件密级类型(1-非涉密,2-秘密[一般],3-机密[重要],4-绝密[非常重要])',
  `USER_PARA` text NOT NULL COMMENT '用户参数',
  `NOT_MOBILE_LOGIN` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '禁止登录OA系统手机客户端(1-禁止,0-不禁止)',
  `MANAGE_MODULE_TYPE` varchar(50) NOT NULL DEFAULT '' COMMENT '用户管理的模块',
  `USER_PRIV_TYPE` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '用户角色类型',
  `USER_MANAGE_ORGS` varchar(200) NOT NULL DEFAULT '' COMMENT '用户管理的分支机构ID串',
  `DEVICE_NUMBER` varchar(50) NOT NULL DEFAULT '' COMMENT '绑定设备号',
  `MOBIL_NO_BD` varchar(20) NOT NULL DEFAULT '' COMMENT '绑定手机号',
  `corp_id` varchar(38) NOT NULL COMMENT '企业ID',
  `THEME_COLOUR` varchar(20) NOT NULL COMMENT '界面主题颜色',
  PRIMARY KEY (`UID`),
  UNIQUE KEY `USER_ID` (`USER_ID`),
  KEY `USER_PRIV` (`USER_PRIV`),
  KEY `DEPT_ID` (`DEPT_ID`),
  KEY `BYNAME` (`BYNAME`),
  KEY `PRIV_NO_NAME` (`USER_PRIV_NO`,`USER_NO`,`USER_NAME`),
  KEY `USER_NAME_INDEX` (`USER_NAME_INDEX`),
  KEY `USER_PRIV_OTHER` (`USER_PRIV_OTHER`),
  KEY `USER_PRIV_TYPE` (`USER_PRIV_TYPE`)
) ENGINE=InnoDB AUTO_INCREMENT=65 DEFAULT CHARSET=gbk COMMENT='用户表';
